function JSSend(method, url, data, callback) {
    var xhr = new XMLHttpRequest();
    var requestUrl = url;
    let Rdata = data
    if (data instanceof FormData) {
        // data = data.inputs
    }
    if (method === 'GET' && data) {
        var params = new URLSearchParams();
        for (var key in data) {
            if (data.hasOwnProperty(key)) {
                params.append(key, data[key]);
            }
        }
        var queryString = params.toString();
        if (queryString) {
            requestUrl += '?' + queryString;
        }
    }
    xhr.open(method, requestUrl, true);
    // xhr.onload = function() {
    //     if (xhr.status >= 200 && xhr.status < 400) {
    //         callback(true, xhr.responseText);
    //     } else {
    //         callback(false, xhr.responseText);
    //     }
    // };
    xhr.onload = function() {
        if (xhr.status >= 200 && xhr.status < 400) {
            // var responseType = xhr.getResponseHeader('X-Response-Type');
            
            var contentType = xhr.getResponseHeader('Content-Type');
                if (contentType && contentType.includes('application/json')) {
                    // La réponse est au format JSON, analyser la réponse JSON
                    var responseJSON = JSON.parse(xhr.responseText);
                    // confirm("su?");
                    callback(true, responseJSON,contentType);
                } else {
                    // La réponse est du contenu HTML, insérer le contenu dans la page
                    // document.getElementById("response").innerHTML = xhr.responseText;
                    
                    callback(true, xhr.responseText,contentType);
                }
                // //console.log(xhr.responseText)
            } else {
                callback(false, xhr.responseText);
            }
        };
    xhr.onerror = function() {
        callback(false, "Une erreur s'est produite lors de la requête.");
    };
    if (method === 'POST') {
        if (Rdata instanceof FormData) {
            // xhr.setRequestHeader('Content-Type', 'multipart/form-data');
        }
        else {
            xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
        }
    }
    xhr.send(data);
}
function Jsend(method, url, data) {
    return new Promise(async (resolve, reject) => {
        try {
            let response;
            if (method === 'GET') {
                let params = new URLSearchParams();
                for (let key in data) {
                    if (data.hasOwnProperty(key)) {
                        params.append(key, data[key]);
                    }
                }
                const queryString = params.toString();
                if (queryString) {
                    url += '?' + queryString;
                }

                response = await fetch(url);
            } else {
                response = await fetch(url, {
                    method: method,
                    body: data,
                });
            }

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const contentType = response.headers.get('Content-Type');
            let responseData;

            if (contentType && contentType.includes('application/json')) {
                responseData = await response.json();
            } else {
                responseData = await response.text();
            }

            resolve(responseData);
        } catch (error) {
            reject(error.message);
        }
    });
}

class tlFormData {
    constructor () {
        this.boundary =  "----MyBoundary" + Math.random().toString(16).substring(2);
        this.data = [];
    }
    append (key, value) {
        this.data.push('--'+this.boundary+'\r\nContent-Disposition: form-data; name="'+key+'"\r\n\r\n'+value+'\r\n');
    }

    appendFile (key, file) {
        this.data.push('--'+this.boundary+'\r\nContent-Disposition: form-data; name="'+key+'"; filename="'+file.name+'"\r\nContent-Type: '+file.type+'\r\n\r\n');
        this.data.push(file);
        this.data.push("\r\n");
    }

    sendData(url) {
        var body = this.data.map(item => {
            if (item instanceof Blob) {
                return new Promise ((resolve,reject)=>{
                    var reader = new FileReader();
                    reader.onload = function (event) {
                        resolve(new Uint8Array(event.target.result));
                    }
                    reader.readAsArrayBuffer(item);
                });
            }
            else {
                return item;
            }
        });

        Promise.all(body).then(parts => {
            var blob = new Blob(parts, {type: "multipart/form-data; boundary="+ this.boundary});

            fetch(url, {
                method: "POST",
                body: blob
            })
            .then(response => response.text())
            .then(result => {
                //console.log(result);
            })
            .catch(error => {
                console.error("Une erreur est survenue lors de l'envoie: "+error);
            });
        });
    }
}

function styleThis(property, value) {
    if (this instanceof NodeList) {
        this.forEach((each)=>{
            each.style[property] = value;
        });
    }
    else {
        this.style[property] = value;
    }
}
function futureBuilder (duration) {
    return new Promise(resolve => {
        setTimeout (()=>{
            resolve()
        },duration)
    })
}
function stylesThis(styleMap) {
    if (typeof styleMap == "object") {
        for (var key in styleMap) {
            if (styleMap.hasOwnProperty(key)) {
                if (this instanceof NodeList) {
                    this.forEach((each)=>{
                        each.style[key] = styleMap[key];
                    });
                }
                else {
                    this.style[key] = styleMap[key];
                } 
            }
        }
        
    }
    else {
        new Error("stylesThis() parameter should be an object")
    }
    
}

