function factoriString(obj) {
    var queryString = "";
    for (var key in obj) {
        if (obj.hasOwnProperty(key)) {
            if (queryString !== "") {
                queryString += "&";
            }
            queryString += encodeURIComponent(key) + "=" + encodeURIComponent(obj[key]);
        }
    }
    return queryString;
}

function stringfyParam(obj, separator = "", addArg = false) {
    var queryString = "";
    var decomp = 0;
    for (var key in obj) {
        if (obj.hasOwnProperty(key)) {
            if (queryString !== "") {
                queryString += separator;
            }
            if (decomp === 0 && addArg) {
                queryString += "?";
                decomp++;
            }
            queryString += encodeURIComponent(key) + "=" + encodeURIComponent(obj[key]);
        }
    }
    return queryString;
}

function simpleStringingMap(obj, separator = "&") {
    var queryString = "";
    for (var key in obj) {
        if (obj.hasOwnProperty(key)) {
            if (queryString !== "") {
                // queryString += separator
                queryString += `&${key}=${obj[key]}`;
            }
            else {
                queryString += `${key}=${obj[key]}`;
            }
            
            // //console.log(`&${key}=${obj[key]}`)
        }
    }
    return queryString;
}

function stringfyThis(obj) {
    var queryString = "";
    for (var key in obj) {
        if (obj.hasOwnProperty(key)) {
            if (queryString !== "") {
                queryString += " ";
            }
            queryString += obj[key];
        }
    }
    return queryString;
}

